<script type="text/javascript">
    let sku_str = "{{$product->id}}";
    $(document).ready(function () {
        (function (doc, win) {
            var docEl = doc.documentElement,
                resizeEvt = 'orientationchange' in window ? 'orientationchange' : 'resize',
                recalc = function () {
                    var clientWidth = docEl.clientWidth;
                    if (!clientWidth) return;
                    docEl.style.fontSize = 16 * (clientWidth / 320) + 'px';
                };
            if (!doc.addEventListener) return;
            win.addEventListener(resizeEvt, recalc, false);
            doc.addEventListener('DOMContentLoaded', recalc, false);
            win.addEventListener('load', recalc, false);
        })(document, window);

        $(window).scroll(function () {
            if ($(this).scrollTop() > 100) {
                $('.back').css('opacity', 1);
            } else {
                $('.back').css('opacity', 0);
            }
        })

        $('.back').click(function () {
            $('html ,body').animate({
                scrollTop: 0
            }, 300);
            return false
        })
    })

    $('#1').click(function () {
        console.log('111111')
        $('#1').addClass('change')
        $('#1 span').addClass('show')
        $('#2 span').removeClass('show')
        $('#1').removeClass('down right left')
        $('#2').addClass('right')
        $('#2').removeClass('down left change')
        // sku_name = '阿里小宝卡';
        sku_str = "2";
    })
    $('#2').click(function () {
        $('#1').addClass('left down')
        $('#2 span').addClass('show')
        $('#1 span').removeClass('show')
        $('#1').removeClass('right change')
        $('#2').addClass('change')
        $('#2').removeClass('down right left')
        // sku_name = '阿里大宝卡';
        sku_str = "519821";
    })
    $('.buttonBox').on('click', function () {
        location.href = '/home/spread/plat_apply?sid=' + sku_str + '&{!!http_build_query($reqParam)!!}';
    });
</script>


