<script type="text/javascript" src="/static/home/public/assets/js/jquery.min.js"></script>
<script type="text/javascript">
    $(document).ready(function () {
        (function (doc, win) {
            var docEl = doc.documentElement,
                resizeEvt = 'orientationchange' in window ? 'orientationchange' : 'resize',
                recalc = function () {
                    var clientWidth = docEl.clientWidth;
                    if (!clientWidth) return;
                    docEl.style.fontSize = 16 * (clientWidth / 320) + 'px';
                };
            if (!doc.addEventListener) return;
            win.addEventListener(resizeEvt, recalc, false);
            doc.addEventListener('DOMContentLoaded', recalc, false);
            win.addEventListener('load', recalc, false);
        })(document, window);

        $(window).scroll(function () {
            if ($(this).scrollTop() > 100) {
                $('.back').css('opacity', 1);
            } else {
                $('.back').css('opacity', 0);
            }
        })

        $('.back').click(function () {
            $('html ,body').animate({
                scrollTop: 0
            }, 300);
            return false
        })
    })


    let sku_str = "{{$reqParam->sid}}";

    $('#1').click(function () {
        $('#1').addClass('checked')
        $('#1').css({
            "transform": "translate3d(0px, 0px, 0px)",
            "transform": "scale(1.3) translateX(0)"
        })
        $('#2').removeClass('checked')
        $('#2').css({
            "transform": "translate3d(120%, 0px, 0px)",
        })
        $('#3').removeClass('checked')
        $('#3').css({
            "transform": "translate3d(240%, 0px, 0px)",
        })
        sku_name = '22卡';
        sku_str = "6";
    })
    if(sku_str === "6"){
        $('#1').click();
    }
    $('#2').click(function () {
        $('#2').addClass('checked')
        $('#2').css({
            "transform": "translate3d(0px, 0px, 0px)",
            "transform": "scale(1.3) translateX(0)"
        })
        $('#1').removeClass('checked')
        $('#1').css({
            "transform": "translate3d(-120%, 0px, 0px)",
        })
        $('#3').removeClass('checked')
        $('#3').css({
            "transform": "translate3d(120%, 0px, 0px)",
        })
        sku_name = '33卡';
        sku_str = "519822";
    })
    $('#3').click(function () {
        $('#3').addClass('checked');
        $('#3').css({
            "transform": "translate3d(0px, 0px, 0px)",
            "transform": "scale(1.3) translateX(0)"
        });
        $('#1').removeClass('checked');
        $('#1').css({
            "transform": "translate3d(-240%, 0px, 0px)",
        });
        $('#2').removeClass('checked');
        $('#2').css({
            "transform": "translate3d(-120%, 0px, 0px)",
        })
        sku_name = '66小电视卡';
        sku_str = "519823";
    });
    $('.buttonBox').on('click', function () {
        location.href = '/home/spread/plat_apply?sid='+sku_str+'&job_number={{$reqParam->job_number}}';
    });
</script>
